pragma solidity ^0.4.18;

contract CaptainGameConfig {
  address owner;
  struct Card {
    uint32 cardId;
    uint32 color;
    uint32 atk_min;
    uint32 atk_max; 
    uint32 defense;
    uint256 price;
    bool unitSellable; 
  }

  /** mapping**/
  mapping(uint256 => Card) private cardInfo;  //normal card
  mapping(uint32 => uint256) public captainIndxToCount;

  function CaptainGameConfig() public {
    owner = msg.sender;

    cardInfo[1] = Card(1, 1, 0, 100, 200,  0.66 ether, true);
    cardInfo[2] = Card(2, 1, 0, 200, 200,  0.66 ether, true);
    cardInfo[3] = Card(3, 2, 0, 300, 100,  0.99 ether, true);
    cardInfo[4] = Card(4, 2, 0, 200, 100,  0.99 ether, true);
    cardInfo[5] = Card(5, 3, 0, 200, 200,  0.5 ether, true);
    cardInfo[6] = Card(6, 3, 0, 300, 400,  0.5 ether, true);
    cardInfo[7] = Card(7, 4, 0, 400, 500, 0.5 ether, true);
    cardInfo[8] = Card(8, 4, 0, 400, 600, 0.3 ether, true);
    cardInfo[9] = Card(9, 4, 0, 500, 600,  0.3 ether, true);

    captainIndxToCount[1] = 30;
    captainIndxToCount[2] = 30;
    captainIndxToCount[3] = 20;
    captainIndxToCount[4] = 20;
    captainIndxToCount[5] = 100;
    captainIndxToCount[6] = 100;
    captainIndxToCount[7] = 100;
    captainIndxToCount[8] = 100;
    captainIndxToCount[9] = 100;
  }

  function getCardInfo(uint32 cardId) external constant returns (uint32, uint32, uint32,uint32,uint256,bool,uint256) {
    return (
      cardInfo[cardId].color, 
      cardInfo[cardId].atk_min,
      cardInfo[cardId].atk_max,
      cardInfo[cardId].defense,
      cardInfo[cardId].price,
      cardInfo[cardId].unitSellable,
      captainIndxToCount[cardId]);
  }    

  function addCard(uint32 id, uint32 color, uint32 atk_min, uint32 atk_max, uint32 defense, uint256 price, bool unitSellable) external {
    require(msg.sender == owner);
    cardInfo[id] = Card(id, color, atk_min, atk_max, defense, price, unitSellable);
  }

  function setCaptainIndexToCount(uint32 _id, uint256 _count) external {
    require(msg.sender == owner);
    captainIndxToCount[_id] = _count;
  }
}
