pragma solidity ^0.4.18;
import "./SafeMath.sol";

interface CaptainTokenInterface {
function getCaptainInfo(uint256 _tokenId) external view returns (
    uint32 captainId,  
    uint32 color, 
    uint32 atk_min,
    uint32 atk_max,
    uint32 defense,
    uint32 level,
    uint256 exp, 
    uint256 price,
    address owner
  );
  function transferFrom(address _from, address _to, uint256 _tokenId) external;
  function setTokenPrice(uint256 _tokenId, uint256 _price) external;
  function CreateCaptainToken(address _owner,uint256 _price, uint32 _captainId, uint32 _color,uint32 _atk_min, uint32 _atk_max,uint32 _defense,uint32 _level,uint256 _exp) public;
}

interface CaptainGameConfigInterface {
  function getCardInfo(uint32 cardId) external constant returns (uint32, uint32, uint32,uint32,uint256,bool,uint256);
}
contract CaptainSell {
  using SafeMath for SafeMath;
  address devAddress;

  function CaptainSell() public {
    devAddress = msg.sender;
  }
  CaptainTokenInterface public captains;
  CaptainGameConfigInterface public config; 
  /// @dev The BuyToken event is fired whenever a token is sold.
  event BuyToken(uint256 tokenId, uint256 oldPrice, address prevOwner, address winner);

  //mapping
  mapping(uint32 => uint256) captainToCount; 

  modifier onlyOwner {
    require(msg.sender == devAddress); 
    _;
  }

  //setting configuration
  function setGameConfigContract(address _address) external onlyOwner {
    config = CaptainGameConfigInterface(_address);
  }

  //setting configuration
  function setCaptainTokenContract(address _address) external onlyOwner {
    captains = CaptainTokenInterface(_address);
  }


  function prepurchase(uint32 _captainId) external payable {
    uint32 color;
    uint32 atk_min;
    uint32 atk_max;
    uint32 defense;
    uint256 price;
    bool unitSellable;
    uint256 captainCount;
    uint256 SellCount = captainToCount[_captainId];
    (color,atk_min,atk_max,defense,price,unitSellable,captainCount) = config.getCardInfo(_captainId);
    require(unitSellable == true);
    SellCount += 1;
    require(SellCount<=captainCount);

    // Safety check to prevent against an unexpected 0x0 default.
    require(msg.sender != address(0));

    // Making sure sent amount is greater than or equal to the sellingPrice
    require(msg.value >= price);
    captains.CreateCaptainToken(msg.sender,price,_captainId,color,atk_min,atk_max,defense,1,0);
    captainToCount[_captainId] = SellCount;
    //event 
    BuyToken(_captainId, price,address(this),msg.sender);
  }

  function getCaptainCount(uint32 _captainId) external view returns (uint256) {
    return captainToCount[_captainId];
  }
  // Allows someone to send ether and obtain the token
  function purchase(uint256 _tokenId) external payable {
    address curOwner;
    uint256 curPrice;
    (,,,,curPrice,curOwner) = captains.getCaptainInfo(_tokenId);
    // Making sure token owner is not sending to self
    require(curOwner != msg.sender);

    // Safety check to prevent against an unexpected 0x0 default.
    require(msg.sender != address(0));

    // Making sure sent amount is greater than or equal to the sellingPrice
    require(msg.value >= curPrice);

    uint256 payment = uint256(SafeMath.div(SafeMath.mul(curPrice, 93), 100));
    uint256 purchaseExcess = SafeMath.sub(msg.value, curPrice); 
    uint256 commission = SafeMath.sub(curPrice,payment); 
    uint256 nextPrice = SafeMath.div(SafeMath.mul(curPrice, 125), 100);

    //update prices
    //IndexToPrice[_tokenId] = nextPrice;
    captains.setTokenPrice(_tokenId,nextPrice);
    //_transfer(oldOwner, msg.sender, _tokenId);
    captains.transferFrom(curOwner,msg.sender, _tokenId);
		// Pay previous tokenOwner if owner is not contract
    if (curOwner != address(this)) {
      curOwner.transfer(payment); //(1-0.07)
    }

    msg.sender.transfer(purchaseExcess);
    devAddress.transfer(commission);
    //event 
    BuyToken(_tokenId, curPrice,curOwner,msg.sender);
  }
}
